$(function() {

  $('.dropdown__toggle').click(function(e) {
    $(this).parent().toggleClass('dropdown--open');
    return false;
  });

});
$(function() {

  $('.toggle').click(function(e) {
    $target = $(this).data('toggle-target');
    $($target).toggleClass('toggle--on');
    return false;
  });

});
